package plugins;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import java.nio.file.Files;
import java.nio.file.Paths;

import static org.junit.jupiter.api.Assertions.*;

class Base64Test {
    private Base64 coder;
    @BeforeEach
    void setUp() {
        coder = new Base64();
    }

    @Test
    void givenText_whenEncoding_thenCorrect(){
        String text = "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.";
        String expected = "";
        assertDoesNotThrow(() -> {
            byte[] data = coder.convert(text.getBytes());
            assertArrayEquals(
                    expected.getBytes(),
                    data
            );
        });
    }
    @Test
    void givenEncodedFile_whenDecoding_thenCorrect(){
        String text = "ывывсфысы";
        assertDoesNotThrow(() -> {
            byte[] encoded = coder.convert(text.getBytes());
            String decoded = new String(coder.revert(encoded));
            assertEquals(text, decoded);
        });
    }
}